<?phpinclude_once('MSQL.php');//// Менеджер для примера//class M_Reg{				    private static $instance; 	// ссылка на экземпляр класса	public $msql;		//	// Получение единственного экземпляра (одиночка)	//	public static function Instance()	{		if (self::$instance == null)			self::$instance = new M_Reg();				return self::$instance;	}	//	// Конструктор	//	public function __construct()	{		$this->msql = MSQL::Instance();  		}		//	// Функция проверки правильности ввода номера телефона	///	public function verPhone($phone_number){	if(preg_match("/^[0-9]{10,10}+$/", $phone_number)){			return false;		}		else{			return true;		}   }		//	// Функция добавления пользователя в БД.	//	public function addUser($object){		$this->msql->Insert('users',$object);		}		//	// Функция обновления пароля.	//	public function recovUser($object, $phone_number){		$where = "phone_number='$phone_number'";		$this->msql->Update('users',$object, $where);	 	}	    //	// Генерация случайной последовательности	// $length 		- ее длина	// результат	- случайная строка	//	public function getCode($length = 6) {		$chars = "0123456789";		$code = "";		$clen = strlen($chars) - 1;  		while (strlen($code) < $length)             $code .= $chars[mt_rand(0, $clen)];  		return $code;	}			//	// Проверка зарегестрирован пользователь уже или нет	//	public function verUser($phone_number) {		$res=$this->msql->Select("SELECT phone_number FROM users WHERE phone_number='$phone_number'");		if(count($res)){			return true;}		else{			return false;		}		}}