<?phpinclude_once('MSQL.php');include_once('M_Users.php');//// Менеджер для примера//class M_SmsRasp{	private static $instance; 	// ссылка на экземпляр класса	private $msql; 				// драйвер БД				//	// Получение единственного экземпляра (одиночка)	//	public static function Instance()	{		if (self::$instance == null)			self::$instance = new M_SmsRasp();				return self::$instance;	}	//	// Конструктор	//	public function __construct()	{		$this->msql   = MSQL::Instance();	}			//	// Функция проверки оформлена ли на пользователя SMS подписка	//	public function verSmsMailing($user)	{		return  $this->msql->Select("SELECT * FROM mailing WHERE (id_user='$user[id_user]' AND message_type='sms')");	}		//	// Функция проверки оформлена ли на пользователя VK подписка	//	public function verVKMailing($user)	{		return  $this->msql->Select("SELECT * FROM mailing WHERE (id_user='$user[id_user]' AND message_type='vk')");	}  	//	// Функция удаления SMS подписки	//	public function delMailing($id_mailing, $id_user)	{		return  $this->msql->Delete('mailing',"id_mailing='$id_mailing' AND id_user='$id_user'");	}						    //	// Функция добавления пользователя в БД.	//	public function add_mailing($object)	{	  		$this->msql->Insert('mailing',$object);			}	//	// А эта функция доступна тем, у кого есть привилегия USE_SECRET_FUNCTIONS.	//	public function MakeSecretMagic($text)	{	    if (!$this->mUsers->Can('USE_SECRET_FUNCTIONS'))		    return null;			return strtoupper($text);	}}